import React, { Component } from 'react';
import './App.css';
import ListWeather from './components/ListWeather';

/**
 * Main component for the Weather-App application
 * 
 * @class App
 * @extends {Component}
 * @version 1.0.0
 * @author {@link https://github.com/camilaavilarinho| Camila Vilarinho}
 * @namespace App
 */
class App extends Component {
  /**
   * Renders the header and the ListWeather component 
   * @memberof App
   */
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <h1 className="App-title">Weather Forecast</h1>
        </header>
        <div className="App-main">
          <ListWeather />
        </div>
      </div>
    );
  }
}

export default App;

import React from 'react';
import { ListGroup, Button, Form, FormGroup } from 'reactstrap';

import Weather from './Weather';
/**
 * List the Weather component
 * @class ListWeather
 * @extends {React.Component}
 */
export default class ListWeather extends React.Component {
    /**
     * Constructor: set the initial state
     * @param {object} props 
     */
    constructor(props) {
        super(props);
        /**
         * @type {object}
         * @property {string} city city's name
         * @property {string} error error message
         * @property {string} isLoaded check if the content was loaded
         * @property {string []} items items resulted from the API request
         */
        this.state = {
            city: 'dublin',
            error: null,
            isLoaded: false,
            items: []
        };
    }
    /**
     * Update the state with the value from input text search
     * @param {string} val input text search value
     */
    componentWillReceiveProps(val) {
        this.setState({
            isLoaded: true,
            city: val
        });
        this.getContent(val);
    }
    /**
     * Handle the text search value sending to componentWillReceiveProps
     * @param {string} val input text search value
     */
    handleSearch(val) {
        this.componentWillReceiveProps(val);
    }

    componentDidMount() {
        this.getContent(this.state.city);
    }

    /**
     * Do a request to the Open Weather forecast API   
     * @see http://openweathermap.org/forecast5 
     * @memberof ListWeather
     * @param {string} val city name 
     * @return {Object} Set the state with the items resulted of the request
     * 
     */

    getContent(val) {
        if (val === '') val = this.state.city;
        fetch(`//api.openweathermap.org/data/2.5/forecast?q=${val}&units=metric&appid=a1bfda65749bde461998c5c598e4964b`)
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        items: result
                    });
                },
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                    console.log(error);
                }
            )

    }
    /**
     * Renders the search form and then the Wheather component
     * 
     * @return {ReactElement} markup for the components
     */
    render() {
        const { error, isLoaded, items } = this.state;
        if (error) {
            return <div>Error: {error.message}</div>;
        } else if (!isLoaded) {
            let inputSearch = null;
            return (
                <div>
                    <Form onSubmit={e => {
                        e.preventDefault()
                        this.handleSearch(inputSearch.value)
                    }}>
                        <FormGroup>
                            <input className="search"
                                ref={node => { inputSearch = node }}
                                placeholder="Search City"
                            />
                        </FormGroup>
                        <Button type="submit">
                            Search
                        </Button>
                    </Form>
                </div>
            );
        } else {
            const weatherList = items.list
                .map(item => (
                    <Weather key={item.dt}
                        item={item}
                    />
                )
                )
            return (
                <div>
                    <h3 className="App-title city">{items.city.name}, {items.city.country}</h3>
                    <ListGroup >
                        {weatherList}
                    </ListGroup>
                </div>
            );
        }
    }
}

import React from 'react';
import { ListGroupItem, ListGroupItemHeading, Badge } from 'reactstrap';
/**
 * Renders an item of WeatherList
 * @param {String}  item represents an item of the WheatherList
 * @return {ReactElement} HTML markup for the component
 */
const Weather = ({ item }) => {
    let key = item.dt;
    let date = item.dt_txt.split(" ");
    let item_date = date[0];
    let item_time = date[1];
    let temp = item.main.temp;
    let min = item.main.temp_min;
    let max = item.main.temp_max;
    let weather_icon = item.weather[0].icon;
    let weather_desc = item.weather[0].description;

    return (
        <ListGroupItem key={key}>
            <div>
                <p className="date">
                    <span><i className="fa fa-calendar icon" aria-hidden="true"></i>{item_date}</span>
                    <span><i className="fa fa-clock-o icon" aria-hidden="true"></i>{item_time}</span>
                </p>
                <img src={`http://openweathermap.org/img/w/${weather_icon}.png`} alt={weather_desc} />
                <br /><span className="weather-desc">{weather_desc}</span>
                <ListGroupItemHeading>
                    <Badge className="main-temp" pill>{temp} ºC</Badge>
                </ListGroupItemHeading>
                <p>
                    <Badge className="temp-min" >{min} ºC</Badge>
                    <Badge className="temp-max" >{max} ºC</Badge>
                </p>

            </div>

        </ListGroupItem>
    );
}

export default Weather;

